import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { ProductModel } from "src/app/models/product.model";
import { ProductService } from "src/app/services/products.service";

@Component({
  selector: "app-product-details",

  templateUrl: "./product-details.component.html",
})
export class ProductDetails {
  theProduct: ProductModel = new ProductModel() ;
  constructor(
    public prdSrvObject: ProductService,
    public route: ActivatedRoute,
  ) {}

  ngOnInit() {
    this.route.params.subscribe((r: any) => {
      const id = +r.id; // converts string to number
      this.theProduct =
        this.prdSrvObject.listofproducts.find(p => p.id === id) ||
        new ProductModel();
    });
  }
}
<div class="row">
  <div class="col-md-9">
    <img [src]="theProduct.imageUrl" [alt]="theProduct.title" width="100%" />
  </div>
  <div class="col-md-3">
    <h5 class="card-title">{{ theProduct.title }}</h5>
    <p class="card-text">
      <app-rating [rating]="theProduct.rating"></app-rating>
    </p>
    <p class="card-text">{{ theProduct.price }}</p>
    <button class="btn btn-primary">
      {{ theProduct.likes }}
      <i class="fa-solid fa-thumbs-up"></i>
    </button>

    <p>
        {{theProduct}}
    </p>
  </div>
</div>
